import {
  IonContent,
  IonIcon,
  IonItem,
  IonLabel,
  IonList,
  IonListHeader,
  IonMenu,
  IonMenuToggle,
  IonItemDivider,
} from '@ionic/react';

import React, { useState } from 'react';
import { archiveOutline, archiveSharp, bookmarkOutline, heartOutline, heartSharp, mailOutline, mailSharp, paperPlaneOutline, paperPlaneSharp, trashOutline, trashSharp, warningOutline, warningSharp, settingsSharp, settingsOutline, logOutOutline, logOutSharp, cartSharp, cardSharp, homeSharp } from 'ionicons/icons';
import './Menu.css';
import tools from './Tools';
import serverVer from './ServerVar';

interface SystemLinks {
  url: string;
  iosIcon: string;
  mdIcon: string;
  title: string;
}

interface UserLinks {
  title: string;
  url: string;
  icon: string;
}

const appPages: SystemLinks[] = [
  {
    title: 'Contact',
    url: 'show-contact-us',
    iosIcon: mailOutline,
    mdIcon: mailSharp
  },
  {
    title: 'Favorites',
    url: '#',
    iosIcon: heartOutline,
    mdIcon: heartSharp
  },
  {
    title: 'Archived',
    url: '#',
    iosIcon: archiveOutline,
    mdIcon: archiveSharp
  },
  {
    title: 'Trash',
    url: '#',
    iosIcon: trashOutline,
    mdIcon: trashSharp
  },
  {
    title: 'Settings',
    url: '#',
    iosIcon: settingsOutline,
    mdIcon: settingsSharp
  }
];

const labels:UserLinks[] = [
  {
    title:'Notification',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'History',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Fault in the area',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Genteral Information',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Chat',
    url:"#",
    icon:bookmarkOutline,
  },
];

const aboutApp:UserLinks[] = [
  {
    title:'About us',
    url:"aboutus",
    icon:bookmarkOutline,
  },
  {
    title:'Privacy and policies',
    url:"show-privacy",
    icon:bookmarkOutline,
  },
  {
    title:'Terms and conditions',
    url:"show-terms",
    icon:bookmarkOutline,
  },
  {
    title:'Help',
    url:"show-help",
    icon:bookmarkOutline,
  },
]

const oftenLink:UserLinks[] = [
  {
    title:'Home',
    url:"home",
    icon:homeSharp,
  },{
    title:'Payment',
    url:"payment",
    icon:cardSharp,
  },
  {
    title:'Logout',
    url:"logout",
    icon:logOutSharp,
  },
]

const Menu: React.FC = () => {
  const [showState, setShowState] = useState9
  return (
    <IonMenu hidden={tools.isLogin()} contentId="menu" type="overlay" 
    style={{width:tools.compare(tools.platform(),true,"","0px")}}>
      <IonListHeader style={{
        fontWeight:"bold",
        fontSize:"25px"
      }}>{tools.MSG.APPNAME}</IonListHeader>

      <IonContent>
        <IonList>
          {oftenLink.map((often, index) => (
            <IonMenuToggle key={index} autoHide={false}>
              <IonItem routerLink="#" onClick={()=>{
                tools.clickById(often.url)
              }} lines="none">
                <IonIcon class="icon" slot="start" icon={often.icon} />
                <IonLabel><div className="title">{often.title}</div></IonLabel>
              </IonItem>
            </IonMenuToggle>
          ))}
        </IonList>

        <IonList>
          <IonItemDivider/>
          {labels.map((label, index) => (
            <IonMenuToggle key={index} autoHide={false}>
              <IonItem routerLink="#" onClick={()=>{
                tools.clickById(label.url)
              }} lines="none">
                <IonIcon class="icon" slot="start" icon={label.icon} />
                <IonLabel><div className="title">{label.title}</div></IonLabel>
              </IonItem>
            </IonMenuToggle>
          ))}
        </IonList>

        <IonList id="inbox-list">
        <IonItemDivider/>
          {appPages.map((appPage, index) => {
            return (
              <IonMenuToggle key={index} autoHide={false}>
                <IonItem routerLink="#" onClick={()=>{
                  tools.clickById(appPage.url)
                }} lines="none" detail={false}>
                  <IonIcon class="icon"  slot="start" ios={appPage.iosIcon} md={appPage.mdIcon} />
                  <IonLabel><div className="title">{appPage.title}</div></IonLabel>
                </IonItem>
              </IonMenuToggle>
            );
          })}
        </IonList>

        <IonList>
          <IonItemDivider/>
            {aboutApp.map((about, index) => {
              return (
                <IonMenuToggle key={index} autoHide={false}>
                  <IonItem routerLink="#" onClick={()=>{
                    tools.clickById(about.url)
                  }} lines="none" detail={false}>
                    <IonIcon class="icon"  slot="start" icon={about.icon}/>
                    <IonLabel><div className="title">{about.title}</div></IonLabel>
                  </IonItem>
                </IonMenuToggle>
              );
            })}
        </IonList>
      </IonContent>
    </IonMenu>
  );
};

export default Menu;
