import React from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";
import { IonPage, IonContent, IonSelect, IonSelectOption, IonLabel, IonItem } from "@ionic/react";
import tools from '../components/Tools';

//import "./styles.css";


const Payment: React.FC = () => {
    const utility = {
        name: "Nawasa",
        price: 64998.67,
        description: "Water company"
    }
    const handleToken = (customer:any) => {
        axios.post(tools.URL.CHECKOUT,{token:customer,product:utility})
        .then(response =>{
            const { status } = response.data;
                console.log("Response:", response.data);
            if (status === "success") {
                console.log("Success! Check email for details");
            } else {
                console.log("Something went wrong");
            }
        })
        .catch(error=>{

        })
        .finally(()=>{

        });
    }
    
    const [company, setCompany]
    return (
        <IonPage>
            <IonContent style={{textAlign:"center"}}>
                <IonItem>
                    <IonLabel>Gender</IonLabel>
                    <IonSelect value={company} placeholder="Select One" onIonChange={e => setCompany(e.detail.value)}>
                        <IonSelectOption value="female">Female</IonSelectOption>
                        <IonSelectOption value="male">Male</IonSelectOption>
                    </IonSelect>
                </IonItem>
                
            </IonContent>
            <div style={{textAlign:"center",width:"100%",marginBottom:"40px",marginTop:"20px"}}>
                <StripeCheckout
                    stripeKey="pk_test_51HMQLOBZvIBjqI0ERBmRc4Feu7qu6fXdnc8IZ9whUpTWAMIEZyYRSUsFCc2LQlXIPJJqBYgzcIbQJY5WODNXdiuf00TucXVjmM"
                    token={token=>{handleToken(token)}}
                    amount={utility.price}
                    name="Tesla Roadster"
                    billingAddress
                    shippingAddress/>
            </div>
        </IonPage>
    );
}


export default Payment
