import {
  IonContent,
  IonIcon,
  IonItem,
  IonLabel,
  IonList,
  IonListHeader,
  IonMenu,
  IonMenuToggle,
  IonItemDivider,
} from '@ionic/react';

import React from 'react';
import { archiveOutline, archiveSharp, bookmarkOutline, heartOutline, heartSharp, mailOutline, mailSharp, paperPlaneOutline, paperPlaneSharp, trashOutline, trashSharp, warningOutline, warningSharp, settingsSharp, settingsOutline, logOutOutline, logOutSharp } from 'ionicons/icons';
import './Menu.css';
import tools from './Tools';
import serverVer from '../components/'

interface SystemLinks {
  url: string;
  iosIcon: string;
  mdIcon: string;
  title: string;
}

interface UserLinks {
  title: string;
  url: string;
  icon: string;
}

const appPages: SystemLinks[] = [
  {
    title: 'Contact',
    url: 'show-contact-us',
    iosIcon: mailOutline,
    mdIcon: mailSharp
  },
  {
    title: 'Favorites',
    url: '#',
    iosIcon: heartOutline,
    mdIcon: heartSharp
  },
  {
    title: 'Archived',
    url: '#',
    iosIcon: archiveOutline,
    mdIcon: archiveSharp
  },
  {
    title: 'Trash',
    url: '#',
    iosIcon: trashOutline,
    mdIcon: trashSharp
  },
  {
    title: 'Settings',
    url: '#',
    iosIcon: settingsOutline,
    mdIcon: settingsSharp
  }
];

const labels:UserLinks[] = [
  {
    title:'Notification',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'History',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Fault in the area',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Genteral Information',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Chat',
    url:"#",
    icon:bookmarkOutline,
  },
];

const aboutApp:UserLinks[] = [
  {
    title:'About us',
    url:"show-about-us",
    icon:bookmarkOutline,
  },
  {
    title:'Privacy and policies',
    url:"show-privacy",
    icon:bookmarkOutline,
  },
  {
    title:'Terms and conditions',
    url:"show-terms",
    icon:bookmarkOutline,
  },
  {
    title:'Help',
    url:"show-help",
    icon:bookmarkOutline,
  },
]

const oftenLink:UserLinks[] = [
  {
    title:'Help',
    url:"show-help",
    icon:bookmarkOutline,
  },{
    title:'Help',
    url:"show-help",
    icon:bookmarkOutline,
  },
]

const Menu: React.FC = () => {
  return (
    <IonMenu contentId="menu" type="overlay" style={{width:"0px"}}>
      <IonListHeader style={{fontWeight:"bold",fontSize:"25px"}}>{systemTools.MSG.APPNAME}</IonListHeader>

      <IonContent>
      <IonMenuToggle autoHide={false}>
          <IonItem routerLink="#" onClick={()=>{
            systemTools.clickById("payment");
          }} lines="full">
            <IonIcon slot="start" icon={logOutSharp}/>
            <IonLabel>Pay Bill</IonLabel>
          </IonItem>
        </IonMenuToggle>

        <IonMenuToggle autoHide={false}>
          <IonItem routerLink="#" onClick={()=>{
            serverVar.cl
            systemTools.logout();
          }} lines="full">
            <IonIcon slot="start" icon={logOutSharp}/>
            <IonLabel>Logout</IonLabel>
          </IonItem>
        </IonMenuToggle>

        <IonList id="labels-list">
          {labels.map((label, index) => (
            <IonMenuToggle key={index} autoHide={false}>
              <IonItem routerLink="#" onClick={()=>{
                systemTools.clickById(label.url)
              }} lines="none">
                <IonIcon slot="start" icon={label.icon} />
                <IonLabel>{label.title}</IonLabel>
              </IonItem>
            </IonMenuToggle>
          ))}
        </IonList>

        <IonList id="inbox-list">
        <IonItemDivider style={{marginBottom:"20px"}}/>
          {appPages.map((appPage, index) => {
            return (
              <IonMenuToggle key={index} autoHide={false}>
                <IonItem routerLink="#" onClick={()=>{
                  systemTools.clickById(appPage.url)
                }} lines="none" detail={false}>
                  <IonIcon slot="start" ios={appPage.iosIcon} md={appPage.mdIcon} />
                  <IonLabel>{appPage.title}</IonLabel>
                </IonItem>
              </IonMenuToggle>
            );
          })}
        </IonList>

        <IonList>
            {aboutApp.map((about, index) => {
              return (
                <IonMenuToggle key={index} autoHide={false}>
                  <IonItem routerLink="#" onClick={()=>{
                    systemTools.clickById(about.url)
                  }} lines="none" detail={false}>
                    <IonIcon slot="start" icon={about.icon}/>
                    <IonLabel>{about.title}</IonLabel>
                  </IonItem>
                </IonMenuToggle>
              );
            })}
        </IonList>
      </IonContent>
    </IonMenu>
  );
};

export default Menu;
