import React, { useState } from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";
import { IonPage, IonContent, IonSelect, IonSelectOption, IonLabel, IonItem, IonCard, IonList, IonText, IonImg } from "@ionic/react";
import tools from '../components/Tools';
import utils from '../Payment/Utils';

//import "./styles.css";


const Payment: React.FC = () => {
    const utility = {
        name: "Nawasa",
        price: 64998.67,
        description: "Water company"
    }
    const handleToken = (customer:any) => {
        axios.post(tools.URL.CHECKOUT,{token:customer,product:utility})
        .then(response =>{
            const { status } = response.data;
                console.log("Response:", response.data);
            if (status === "success") {
                console.log("Success! Check email for details");
            } else {
                console.log("Something went wrong");
            }
        })
        .catch(error=>{

        })
        .finally(()=>{

        });
    }
    
    const [selectName, setSelectName] = useState();
    const [getData, setData] = useState({name:'',price:"",description:"",image:"",});
    return (
        <IonPage>
            <IonContent style={{textAlign:"center",border:"1px"}}>
                <IonCard style={{marginTop:"20%"}}>
                    <IonItem style={{marginRight:"20px",marginLeft:"20px"}}>
                        <IonLabel>Company?</IonLabel>
                        <IonSelect value={selectName} interface="popover" placeholder="Empty" onIonChange={e =>{
                            setSelectName(e.detail.value);
                            setData(e.detail.value);
                        }}>
                            {
                                utils.utility.map((util:any,i:number) =>{
                                    return(
                                        <IonSelectOption key={i} value={util} >{util.name}</IonSelectOption>
                                    )
                                })
                            }
                        </IonSelect>
                    </IonItem>
                </IonCard>
                    
                <IonCard style={{marginTop:"20%"}}>
                    <IonList>
                        <IonItem>
                            <IonImg style={{width:"100%",height:"100%"}} src={getData.image}/>
                        </IonItem>
                        <IonItem style={{textAlign:"center"}}>
                            <IonLabel>{getData.name}</IonLabel>
                        </IonItem>
                        <IonItem style={{textAlign:"center"}}>
                            <IonLabel>{getData.price}</IonLabel>
                        </IonItem>
                        <IonItem>
                            <IonText>{getData.description}</IonText>
                        </IonItem>
                    </IonList>    
                </IonCard>
            </IonContent>
            <div style={{textAlign:"center",width:"100%",marginBottom:"40px",marginTop:"20px"}}>
                <StripeCheckout
                    stripeKey="pk_test_51HMQLOBZvIBjqI0ERBmRc4Feu7qu6fXdnc8IZ9whUpTWAMIEZyYRSUsFCc2LQlXIPJJqBYgzcIbQJY5WODNXdiuf00TucXVjmM"
                    token={token=>{handleToken(token)}}
                    amount={utility.price}
                    name="Tesla Roadster"
                    billingAddress
                    shippingAddress/>
            </div>
        </IonPage>
    );
}


export default Payment
