import React, { useState } from "react";
import StripeCheckout from "react-stripe-checkout";
import axios from "axios";
import { IonPage, IonContent, IonSelect, IonSelectOption, IonLabel, IonItem, IonCard, IonList, IonText, IonImg, IonThumbnail, IonToolbar, IonTitle, IonHeader, IonSplitPane, IonButtons, IonBackButton, IonIcon } from "@ionic/react";
import tools from '../components/Tools';
import utils from '../Payment/Utils';
import Info from '../Payment/Info';
import { arrowBack } from "ionicons/icons";
//import "./styles.css";


const Payment: React.FC = () => {
    const handleToken = (customer:any) => {
        axios.post(tools.URL.CHECKOUT,{token:customer,product:utils.getData()})
        .then(response =>{
            const { status } = response.data;
                console.log("Response:", response.data);
            if (status === "success") {
                console.log("Success! Check email for details");
            } else {
                console.log("Something went wrong");
            }
        })
        .catch(error=>{

        })
        .finally(()=>{

        });
    }
    
    const [isHiden, setHIden] = useState(true);
    const [selectName, setSelectName] = useState();
    const [getData, setData] = useState({name:'',price:"",description:"",image:"",});
    const RIGHTSIDEWIDTH = tools.compare(tools.platform(),true,"","400PX");
    const LEFTSIDEWIDTH = tools.compare(tools.platform(),true,"","600PX");
    const FLOATlEFT = tools.compare(tools.platform(),true,"","left");
    const FLOATRIGHT = tools.compare(tools.platform(),true,"","right");
    return (
        <IonPage style={{border:"1px solid gray"}}>
            <IonHeader>
                <IonToolbar>
                    <IonButtons slot="start">
                        <IonBackButton defaultHref=""/>
                    </IonButtons>
                    <IonTitle style={{color:"teal",fontWeight:"bold",fontSize:"30px"}}>Payments</IonTitle>
                </IonToolbar>
            </IonHeader>

            <IonContent>
                <IonList style={{width:RIGHTSIDEWIDTH,height:"100%",float:FLOATlEFT}}>
                    <IonCard style={{marginTop:"10%",border:"1px solid lightgray"}}>
                        <IonItem style={{marginRight:"20px",marginLeft:"20px"}}>
                            <IonLabel style={{color:"teal"}}>Bill paying too:</IonLabel>
                            <IonSelect value={selectName} interface="popover" placeholder="choose" onIonChange={e =>{
                                setSelectName(e.detail.value);
                                setData(e.detail.value);
                                setHIden(false);
                                var customData = e.detail.value;
                                delete customData.image;
                                utils.holdData(customData);
                            }}>
                                {
                                    utils.utility.map((util:any,i:number) =>{
                                        return(
                                            <IonSelectOption key={i} value={util} >{util.name}</IonSelectOption>
                                        )
                                    })
                                }
                            </IonSelect>
                        </IonItem>
                    </IonCard>

                    {/*this is the info card and display company selected data*/}
                    <IonContent style={{textAlign:"center",height:tools.compare(tools.platform(),true,"60%","50%")}}>
                        <IonCard hidden={isHiden} style={{marginTop:"20%",border:"1px solid lightgray"}}>
                            <IonList>
                                <IonThumbnail style={{width:"100%",height:"50%"}}>
                                    <IonImg style={{width:"100%",maxHeight:"100%"}} src={getData.image}/>
                                </IonThumbnail>
                                <IonItem style={{textAlign:"center"}} lines="full">
                                    <IonLabel>{getData.name}</IonLabel>
                                </IonItem>
                                <IonItem style={{textAlign:"center"}} lines="full">
                                    <IonLabel>{getData.price}</IonLabel>
                                </IonItem>
                                <IonItem lines="full">
                                    <IonText>{getData.description}</IonText>
                                </IonItem>
                            </IonList>    
                        </IonCard>

                        {/*this card will show first or if the infor card is not active*/}
                        <IonCard hidden={!isHiden} style={{marginTop:"1%",border:"1px solid lightgray"}}>
                            <IonList>
                                <IonThumbnail style={{width:"50%",height:"50%"}}>
                                    <IonImg style={{width:"100%",maxHeight:"100%"}} src={utils.dummyData.image}/>
                                </IonThumbnail>
                                <IonItem style={{textAlign:"center"}} lines="full">
                                    <IonLabel>{utils.dummyData.name}</IonLabel>
                                </IonItem>
                                <IonItem style={{textAlign:"center"}} lines="full">
                                    <IonLabel>{utils.dummyData.price}</IonLabel>
                                </IonItem>
                                <IonItem lines="full">
                                    <IonText>{utils.dummyData.description}</IonText>
                                </IonItem>
                            </IonList>    
                        </IonCard>    

                                 
                                                
                    </IonContent> 
                    <IonItem style={{textAlign:"center"}} lines="none">
                        <IonLabel>{getData.price}</IonLabel>
                    </IonItem>
                    <div style={{textAlign:"center",width:"100%",marginTop:"-50"}}>
                        <StripeCheckout
                            stripeKey="pk_test_51HMQLOBZvIBjqI0ERBmRc4Feu7qu6fXdnc8IZ9whUpTWAMIEZyYRSUsFCc2LQlXIPJJqBYgzcIbQJY5WODNXdiuf00TucXVjmM"
                            token={token=>{handleToken(token)}}
                            amount={parseFloat(getData.price.replace("$","")) * 100}
                            name="Tesla Roadster"
                            billingAddress
                            shippingAddress/>
                    </div>
                </IonList>

                {/*this section is the side of the payment string for information*/}
                <IonList style={{width:LEFTSIDEWIDTH,height:"100%",float:FLOATRIGHT}}>
                    <Info/>
                </IonList>
            </IonContent>
        </IonPage>
    );
}


export default Payment
