import {
  IonContent,
  IonIcon,
  IonItem,
  IonLabel,
  IonList,
  IonListHeader,
  IonMenu,
  IonMenuToggle,
  IonNote,
  IonItemDivider,
} from '@ionic/react';

import React from 'react';
import { archiveOutline, archiveSharp, bookmarkOutline, heartOutline, heartSharp, mailOutline, mailSharp, paperPlaneOutline, paperPlaneSharp, trashOutline, trashSharp, warningOutline, warningSharp, settingsSharp, settingsOutline } from 'ionicons/icons';
import './Menu.css';
import systemTools from './Tools';

interface SystemLinks {
  url: string;
  iosIcon: string;
  mdIcon: string;
  title: string;
}

interface UserLinks {
  title: string;
  url: string;
  icon: string;
}

const appPages: SystemLinks[] = [
  {
    title: 'Contact',
    url: 'show-contact-us',
    iosIcon: mailOutline,
    mdIcon: mailSharp
  },
  {
    title: 'Favorites',
    url: '#',
    iosIcon: heartOutline,
    mdIcon: heartSharp
  },
  {
    title: 'Archived',
    url: '#',
    iosIcon: archiveOutline,
    mdIcon: archiveSharp
  },
  {
    title: 'Trash',
    url: '#',
    iosIcon: trashOutline,
    mdIcon: trashSharp
  },
  {
    title: 'Settings',
    url: '#',
    iosIcon: settingsOutline,
    mdIcon: settingsSharp
  }
];

const labels:UserLinks[] = [
  {
    title:'Notification',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'History',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Fault in the area',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Genteral Information',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Chat',
    url:"#",
    icon:bookmarkOutline,
  },
];

const aboutApp:UserLinks[] = [
  {
    title:'About us',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'About us',
    url:"#",
    icon:bookmarkOutline,
  },
  {
    title:'Help',
    url:"#",
    icon:bookmarkOutline,
  },
]

const Menu: React.FC = () => {
  return (
    <IonMenu contentId="menu" type="overlay" style={{width:"0px"}}>
      <IonContent>
        <IonList id="inbox-list">
        <IonListHeader>Client Service</IonListHeader>

        <IonList id="labels-list">
          <IonListHeader>Links</IonListHeader>
          {labels.map((label, index) => (
            <IonMenuToggle key={index} autoHide={false}>
              <IonItem routerLink="#" onClick={()=>{
                systemTools.clickById(label.url)
              }} lines="none">
                <IonIcon slot="start" icon={label.icon} />
                <IonLabel>{label.title}</IonLabel>
              </IonItem>
            </IonMenuToggle>
          ))}
        </IonList>
        <IonItemDivider style={{marginBottom:"20px"}}/>
        <IonNote>Options</IonNote>
          {appPages.map((appPage, index) => {
            return (
              <IonMenuToggle key={index} autoHide={false}>
                <IonItem routerLink="#" onClick={()=>{
                  systemTools.clickById(appPage.url)
                }} lines="none" detail={false}>
                  <IonIcon slot="start" ios={appPage.iosIcon} md={appPage.mdIcon} />
                  <IonLabel>{appPage.title}</IonLabel>
                </IonItem>
              </IonMenuToggle>
            );
          })}
        </IonList>

        <IonList>
          <IonNote>This App</IonNote>
            {aboutApp.map((about, index) => {
              return (
                <IonMenuToggle key={index} autoHide={false}>
                  <IonItem routerLink="#" onClick={()=>{
                    systemTools.clickById(about.url)
                  }} lines="none" detail={false}>
                    <IonIcon slot="start" icon={about.icon}/>
                    <IonLabel>{about.title}</IonLabel>
                  </IonItem>
                </IonMenuToggle>
              );
            })}
        </IonList>
      </IonContent>
    </IonMenu>
  );
};

export default Menu;
